      </button>
      <div className="content card-transition">
        <Card key={currentCardIndex} title={data[currentCardIndex].title} content={data[currentCardIndex].content} />
        <Card key={currentCardIndex + 1} title={data[currentCardIndex + 1].title} content={data[currentCardIndex + 1].content} />
        <Card key={currentCardIndex + 2} title={data[currentCardIndex + 2].title} content={data[currentCardIndex + 2].content} />
        <Card key={currentCardIndex + 3} title={data[currentCardIndex + 3].title} content={data[currentCardIndex + 3].content} />
      </div>
      <button onClick={handleNextCard} disabled={currentCardIndex === data.length - 4} className='arrow'>Next</button>
    </section>
  );
};

export default CardList;
