import React from 'react';
import Card from './card';
import "../App.css"
import { useState } from 'react';

const CardList = ({ data }) => {
  const [currentCardIndex, setCurrentCardIndex] = useState(0);

  const handlePreviousCard = () => {
    setCurrentCardIndex((prevIndex) => prevIndex - 1);
  };

  const handleNextCard = () => {
    setCurrentCardIndex((prevIndex) => prevIndex + 1);
  };

  return (
    <section className="content">
      <div className="arrow-buttons">
        <button onClick={handlePreviousCard} disabled={currentCardIndex === 0}>Previous</button>
        <button onClick={handleNextCard} disabled={currentCardIndex === data.length - 1}>Next</button>
      </div>
      <Card key={currentCardIndex} title={data[currentCardIndex].title} content={data[currentCardIndex].content} />
      <Card key={currentCardIndex+1} title={data[currentCardIndex+1].title} content={data[currentCardIndex+1].content} />
    </section>
  );
};

export default CardList;
