import React from 'react';
import Card from './card';
import "../App.css"
import { useState } from 'react';
import left_arrow from "../images/left_arrow.png"
import right_arrow from "../images/right_arrow.png"

const CardList = ({ data }) => {
  const [currentCardIndex, setCurrentCardIndex] = useState(0);

  const handlePreviousCard = () => {
    setCurrentCardIndex((prevIndex) => prevIndex - 1);
  };

  const handleNextCard = () => {
    setCurrentCardIndex((prevIndex) => prevIndex + 1);
  };

  return (
    <div>
    <button onClick={handlePreviousCard} disabled={currentCardIndex === 0} style={{ border: 0 }} >
    <img src={left_arrow} className='left_arrow' />
  </button>
    <section className="content">
      <div className='content'>
        <Card key={currentCardIndex} title={data[currentCardIndex].title} content={data[currentCardIndex].content} />
        <Card key={currentCardIndex + 1} title={data[currentCardIndex + 1].title} content={data[currentCardIndex + 1].content} />
        <Card key={currentCardIndex + 2} title={data[currentCardIndex + 2].title} content={data[currentCardIndex + 2].content} />
        <Card key={currentCardIndex + 3} title={data[currentCardIndex + 3].title} content={data[currentCardIndex + 3].content} />
      </div>
      <button onClick={handleNextCard} disabled={currentCardIndex === data.length - 4} style={{ border: 0 }}  >
        <img src={right_arrow} className='right_arrow' />
      </button>
    </section>
    </div>
  );
};

export default CardList;
