import { useState } from "react";
import React from "react";
import "../App.css";

const Alertreq = () => {
    return (
        <div className="req_field">
            <p className="req_filed_text">* required field</p>
        </div>)
}

const Alertinvalid = ({ message, flag }) => {
    return (
        <div>
            {flag} && <div className="alert_box">
                <div className="alertinvalid">
                    {message}
                </div>
            </div>
        </div>
    );
};

export { Alertreq, Alertinvalid }
