import React from 'react'
import { NavLink } from "react-router-dom"
import 'bootstrap/dist/css/bootstrap.css'
import "../App.css"
import Navbar from './Navbar'
import Card from './Card';

const data = [
    { title: "Card 1", content: "This is the content of Card 1." },
    { title: "Card 2", content: "This is the content of Card 2." },
    { title: "Card 3", content: "This is the content of Card 3." },
  ];

  function generateCards() {
    const cardContainer = document.getElementById("cardContainer");

    // Clear the container before generating new cards (optional)
    cardContainer.innerHTML = "";

    // Loop through the data array and create a card for each item
    data.forEach((item) => {
      const card = document.createElement("div");
      card.className = "card";

      // Create the card content
      const titleElement = document.createElement("h2");
      titleElement.textContent = item.title;

      const contentElement = document.createElement("p");
      contentElement.textContent = item.content;

      // Append title and content elements to the card
      card.appendChild(titleElement);
      card.appendChild(contentElement);

      // Append the card to the container
      cardContainer.appendChild(card);
    });
  }

  // Call the function to generate cards when the page loads
  generateCards();


export const Content = () => {
    return (
        <div>
            <Navbar />
            <section className='display'>
                <h1 className='heading1'>Beginner friendly recipes</h1>
                <h3 className='heading2'>Cooking is fun with us</h3>
            </section>
            <br/>
            <h1 className='heading3'>Something</h1>
            <br/>
            <section className='content border_all'>

            </section>
        </div>
    )
}

export default Content
